import './header.style.scss';
import 'tippy.js/dist/svg-arrow.css';
import SearchIcon from '@mui/icons-material/Search';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import React, { useState } from 'react';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import HeadlessTippy from '@tippyjs/react/headless'; //

const MENU_ITEMS = [
  {
    title: 'Home',
    icon: <KeyboardArrowDownIcon className="icon" />,
    data: [
      {
        title: 'Home 01',
      },
      {
        title: 'Home 02',
      },
      {
        title: 'Home 03',
      },
      {
        title: 'Home 04',
      },
      {
        title: 'Home 05',
      },
      {
        title: 'Home 06',
      },
    ],
  },
  {
    title: 'Explore',
    icon: <KeyboardArrowDownIcon className="icon" />,
    data: [
      {
        title: 'Explore 1',
      },
      {
        title: 'Explore 2',
      },
      {
        title: 'Explore 3',
      },
      {
        title: 'Explore 4',
      },
    ],
  },
  {
    icon: <KeyboardArrowDownIcon className="icon" />,
    title: 'Activity',
    data: [
      {
        title: 'Activity 1',
      },
      {
        title: 'Activity 2',
      },
    ],
  },
  {
    title: 'Community',
    icon: <KeyboardArrowDownIcon className="icon" />,
    data: [
      { title: 'Blog' },
      { title: 'Blog Detail' },
      { title: 'Help Center' },
    ],
  },
  {
    title: 'Page',
    icon: <KeyboardArrowDownIcon className="icon" />,
    data: [{ title: 'Author 1' }, { title: 'Author 2' }, { title: 'Author 3' }],
  },
  {
    icon: <KeyboardArrowDownIcon className="icon" />,
    title: 'Contact',
    data: [{ title: 'Contact 2' }, { title: 'Contact 1' }],
  },
];

const HeadlessTippyItem = ({ item }) => {
  const [isHover, setIsHover] = useState(false);

  const onHover = () => {
    setIsHover(!isHover);
  };
  return (
    <li className="menu-item-title">
      <h4 onMouseEnter={onHover} onMouseLeave={onHover} className="item-title">
        {isHover && <span className="divider"></span>}
        <div className="title">{item.title}</div>
      </h4>
    </li>
  );
};

const Tippy = ({ item }) => {
  return (
    <HeadlessTippy
      interactive
      delay={[0, 50]}
      offset={[-10, 20]}
      placement="bottom-start"
      render={attrs => (
        <div className="box" tabIndex="-1" {...attrs}>
          {item.data.map(i => (
            <HeadlessTippyItem key={i.title} item={i} />
          ))}
        </div>
      )}
    >
      <div>{item.title}</div>
    </HeadlessTippy>
  );
};

const Header = () => {
  const [search, setSearch] = useState(false);
  const handlerSearch = () => {
    setSearch(!search);
  };

  return (
    <header className="wrapper">
      <div className="inner">
        <div className="logo">
          <img
            src="data:image/png;base64,"
            alt="axies"
          />
        </div>
        <div className="main-nav">
          {MENU_ITEMS.map(item => (
            <div key={item.title} className="title">
              <Tippy item={item} />
              <div>{item.icon}</div>
            </div>
          ))}
        </div>

        <div className="flat-search">
          <SearchIcon className="icon-search" onClick={handlerSearch} />
          <button className="out">
            <AccountBalanceWalletIcon />
            <span>Wallet Connect</span>
          </button>
          {search && (
            <div className="search-btn">
              <input type="text" placeholder="Search..." />
              <SearchIcon className="icon-search-btn" />
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
